<?php/** * Created by PhpStorm. * User: xxx * Date: 2018/3/18 * Time: 14:53 */namespace backend\models;use common\models\gii\CustomerGii;use Yii;use yii\base\Exception;class Customer extends CustomerGii{    public function getPassword($password){        return Yii::$app->security->generatePasswordHash($password);    }    public function validatePassword($password, $hash)    {        return Yii::$app->security->validatePassword($password, $hash);    }    public function setPassword($password)    {        $this->password = Yii::$app->security->generatePasswordHash($password);    }}